use oxemarine_ldw
GO

if not exists (select * from sys.external_data_sources where name='oxemarine_src')
    CREATE EXTERNAL DATA SOURCE oxemarine_src
    WITH (
        LOCATION    = 'https://stdetoxemarine.dfs.core.windows.net/fs-oxe'
        --LOCATION    = 'https://stoxemarine.blob.core.windows.net/fs-oxe-bronze'
    )
    ;

use oxemarine_ldw
GO

if not exists (select * from sys.external_file_formats where name='csv_file_format')
--Create an external file format for DELIMITED TEXT files
    CREATE EXTERNAL FILE FORMAT csv_file_format  
    WITH (  
        FORMAT_TYPE = DELIMITEDTEXT  
        , FORMAT_OPTIONS ( FIELD_TERMINATOR = ','  
        , STRING_DELIMITER = '"'
        , FIRST_ROW = 2
        , USE_TYPE_DEFAULT =  FALSE
        , ENCODING = 'UTF8'
        , PARSER_VERSION = '2.0' )   
        )
    ;  

if not exists (select * from sys.external_file_formats where name='csv_file_format_pv1')
--Create an external file format for DELIMITED TEXT files
    CREATE EXTERNAL FILE FORMAT csv_file_format_pv1
    WITH (  
        FORMAT_TYPE = DELIMITEDTEXT  
        , FORMAT_OPTIONS ( FIELD_TERMINATOR = ','  
        , STRING_DELIMITER = '"'
        , FIRST_ROW = 2
        , USE_TYPE_DEFAULT =  FALSE
        , ENCODING = 'UTF8'
        , PARSER_VERSION = '1.0' )   
        )
    ; 

if not exists (select * from sys.external_file_formats where name='parquet_file_format')
CREATE EXTERNAL FILE FORMAT parquet_file_format
    WITH (  
        FORMAT_TYPE = PARQUET
        , DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
    );

if not exists (select * from sys.external_file_formats where name='parquet_file_format_gz')
CREATE EXTERNAL FILE FORMAT parquet_file_format_gz
    WITH (  
        FORMAT_TYPE = PARQUET
        , DATA_COMPRESSION = 'org.apache.hadoop.io.compress.GzipCodec'
    );

  if not exists (select * from sys.external_file_formats where name='delta_file_format')
CREATE EXTERNAL FILE FORMAT delta_file_format
    WITH (  
        FORMAT_TYPE = DELTA
        , DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
    );
